import {
  ChangeDetectionStrategy,
  Component,
  EventEmitter,
  Input,
  Output,
  ViewEncapsulation,
} from '@angular/core';
import { PokemonTypes } from '../../dto/pokemonTypes';
import { SinglePokemonInfo } from '../../dto/singlePokemonInfo.dto';

@Component({
  selector: 'pokemon-card',
  templateUrl: './pokemon-card.component.html',
  styleUrls: ['./pokemon-card.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  encapsulation: ViewEncapsulation.Emulated,
})
export class PokemonCardComponent {
  @Input() pokemon: SinglePokemonInfo;

  @Input() activePokemon: string;

  @Output() clickFunc = new EventEmitter<string>();

  public pokemonTypes: any = PokemonTypes;

  public setIndex(name: string) {
    this.clickFunc.emit(name);
  }
}
<p-card
  [header]="pokemon.name | upperCaseLetter"
  [style]="{
    width: '17rem',
    padding: '1rem',
    display: 'flex',
    'flex-direction': 'column'
  }"
  (click)="setIndex(pokemon.name)"
  [styleClass]="activePokemon === pokemon.name ? 'active' : ''"
  class="card"
>
  <ng-template pTemplate="header">
    <img
      alt="Card"
      [src]="pokemon.sprites.front_default"
      [style]="{ width: '100px' }"
      *ngIf="pokemon.sprites.front_default"
    />
    <img
      alt="Card"
      [src]="'https://js-pokedex-virid.vercel.app/src/no-pokemon-selected-image.png'"
      [style]="{ width: '70px' }"
      *ngIf="!pokemon.sprites.front_default"
    />
  </ng-template>
  <div class="container">
    <div *ngFor="let type of pokemon.types | slice : 0 : 2">
      <p-chip
        [label]="type.type.name | upperCaseLetter"
        [style]="{
          'margin-right': '0px',
          background: pokemonTypes[type.type.name],
          color: 'white'
        }"
      ></p-chip>
    </div>
  </div>
</p-card>
